import React from "react";
import { useHistory } from "react-router-dom";
import "./MovieCard.css";

const IMGPATH = "https://image.tmdb.org/t/p/w1280";

const MovieCard = (props) => {
    const { data } = props;
    const history = useHistory();

    // Use optional chaining to safely access nested properties
    const imageUrl = data?.imageurl;
    const title = data?.title;
    const releaseYear = data?.released;
    const movieId = data?.imdbid; // Assuming you have an ID for each movie
    const genres = data?.genres || []; // Assuming genres is an array

    const handleClick = () => {
        history.push(`/movie/${movieId}`);
    };

    return (
        <div className="card-item" onClick={handleClick} style={{ cursor: 'pointer' }}>
            <div className="card-inner">
                <div className="card-top">
                    {imageUrl ? (
                        <img src={imageUrl} alt={title} />
                    ) : (
                        <img src={IMGPATH} alt={title} />
                    )}
                </div>
                <div className="card-bottom">
                    <div className="card-info">
                        <h4>{title}</h4>
                        <p>{releaseYear} &nbsp; &nbsp; &nbsp; type: {data.type}</p>
                        <p style={{ margin: '10px 0px' }}>synopsis: {data.synopsis}</p>
                        <p>Genres:</p>
                        <div className="genres">
                            {genregenres.map((genre, index) => (
                                <span key={index} className="genre-tag">{genre}</span>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MovieCard;
