import React, { useState, useEffect } from 'react';
import movieApi from "../common/apis/movieApi";
import { useParams } from 'react-router-dom'; 

const MovieDetail = () => {
    const [movie, setMovie] = useState(null);
    const [loading, setLoading] = useState(true);
    let { imdbID } = useParams();

    useEffect(() => {
        const fetchMovie = async () => {
            try {
                setLoading(true);
                const response = await movieApi.get(`/titles/${imdbID}`);
                setMovie(response.data); // Assuming response.data contains movie details
                setLoading(false);
            } catch (error) {
                console.error('Error fetching movie:', error);
                setLoading(false);
            }
        };

        fetchMovie();
    }, [imdbID]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!movie) {
        return <div>Movie not found</div>;
    }

    return (
        <div>
            <h1 style={{color: white}}>{movie.titleText ? movie.titleText.text : ''}</h1>
            <p>Release Year: {movie.releaseYear ? movie.releaseYear.year : ''}</p>
            <p>Genre: {movie.genre}</p> {/* Assuming `genre` is available in `movie` */}
            <p>Plot: {movie.plot}</p> {/* Assuming `plot` is available in `movie` */}
            <p>Director: {movie.director}</p> {/* Assuming `director` is available in `movie` */}
            {movie.primaryImage && (
                <img 
                    src={movie.primaryImage.url} 
                    alt={movie.titleText ? movie.titleText.text : ''} 
                    width={movie.primaryImage.width} 
                    height={movie.primaryImage.height} 
                />
            )}
            {/* Add more details as needed */}
        </div>
    );
}

export default MovieDetail;
