import React, { useState, useEffect } from 'react';
import movieApi from "../common/apis/movieApi";
import { useParams } from 'react-router-dom'; 
const MovieDetail = () => {
    const [movie, setMovie] = useState(null);
    const [loading, setLoading] = useState(true);
    let { id } = useParams();
    useEffect(() => {

        const fetchMovie = async () => {
            try {
                setLoading(true);
                const response = await movieApi.get(`/titles/${id}`);
                setMovie(response.data); // Assuming response.data contains movie details
                setLoading(false);
            } catch (error) {
                console.error('Error fetching movie:', error);
                setLoading(false);
            }
        };

        fetchMovie();
    }, [id]); // Trigger fetch on component mount and when match.params.id changes

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!movie) {
        return <div>Movie not found</div>; // Handle case where movie is not found
    }

    return (
        <div>
            <h1>{movie.title}</h1>
            <p>Release Year: {movie.releaseYear}</p>
            <p>Genre: {movie.genre}</p>
            <p>Plot: {movie.plot}</p>
            <p>Director: {movie.director}</p>
            {/* Add more details as needed */}
        </div>
    );
}

export default MovieDetail;
